// Sample of parsing raw CI-V packets
// Mode 3 Get Frequency
// Warren, KD4Z
// Modify by Real VE2CUZ


const CMD_GETFREQ = 0x03;
const RADIO_ADDR = 0x88;    // change this to your radio address
const MASK_HIGHNIB = 0xF0;
const MASK_LOWNIB = 0x0F;

var addDebug = true;
var dataStartOffset = 5;

var sender = 0;
var destination = 0;
var command = 0;

var MHZ = 0;
var mhz = 0; // Save var on FLOW

var KHZ = 0;
var khz = 0; // Save var on FLOW

var HZ = 0;
var hz = 0; // Save var on FLOW

var FREQ = 0;

destination = msg.payload[2];
sender      = msg.payload[3];
command     = msg.payload[4];

if (addDebug)
{
    msg.sender      = sender;
    msg.destination = destination;
    msg.command     = command;
}

if (command == CMD_GETFREQ && sender == RADIO_ADDR )
{
    //////////////////
    // Save on FLOW //
    ///////////////////////////////////////////////
    flow.set('FREQ',msg.payload);

    mhz = msg.payload[8];
    flow.set('MHZ',mhz);

    khz = msg.payload[7];
    flow.set('KHZ',khz);

    hz = msg.payload[6];
    flow.set('HZ',hz);
    ///////////////////////////////////////////////

    ofs = dataStartOffset;  // [0x70]

    HZ  =  ((msg.payload[ofs] & MASK_HIGHNIB) / 16 ) * 10; // highnibble
    HZ  += ((msg.payload[ofs] & MASK_LOWNIB)      );       // lownibble    

    ofs ++;  // 6 [0x56]

    KHZ =  (msg.payload[ofs] & MASK_HIGHNIB) / 16;        // highnibble
    HZ  += ((msg.payload[ofs] & MASK_LOWNIB) * 100);      // lownibble

    ofs ++;  // 7 [0x34]

    KHZ += (((msg.payload[ofs] & MASK_HIGHNIB) / 16 ) * 100);  // highnibble
    KHZ += ((msg.payload[ofs] & MASK_LOWNIB )        * 10);    // lownibble

    ofs ++;  // 8 [0x12]

    MHZ =  (((msg.payload[ofs] & MASK_HIGHNIB) / 16 )  * 10);  // highnibble
    MHZ += ((msg.payload[ofs] & MASK_LOWNIB )             );   // lownibble

    FREQ = (MHZ * 1000) + KHZ + (HZ / 1000);
    msg.freq = FREQ;

    if (MHZ < 10){
        MHZ = "0" + MHZ;
    }

    if (KHZ < 99 ){
        KHZ = "0" + KHZ;
        if (KHZ < 10){
            KHZ = "0" + KHZ;
        }
    }

    msg.MHZ = MHZ;
    msg.KHZ = KHZ;

    if (addDebug)
    {
        msg.MHZ = MHZ;  // 12
        msg.KHZ = KHZ;  // 345
        msg.HZ  = HZ;   // 67
    }

    //if find error dont update DashBoard
    if (MHZ <= 30){
        return msg;
    }
}

